tinymce.PluginManager.add("imageserveur", function(e, t) {
	e.addMenuItem("imgserveur", {
		text : "Ins√©rer image",
		context : "tools",
		onclick : function() {
			e.windowManager.open({
				title : "TinyMCE site",
				url : t,
				width : 600,
				height : 400,
				buttons : [
						{
							text : "Insert",
							onclick : function() {
								var t = e.windowManager.getWindows()[0];
								e.insertContent("<img src=\"/imgs/imageDisplay?id=" + t.getContentWindow().document
										.getElementById("content").value + "\" />"), t
										.close()
							}
						}, {
							text : "Close",
							onclick : "close"
						} ]
			})
		}
	})
});